$a = winget upgrade; $p = "$env:temp/apps-to-update.txt"; "You will update these apps:                       $a                                                                            IN 10 SECONDS. TO STOP UPDATING CLOSE POWERSHELL" | Out-File -FilePath $p -Force; Start-Process notepad $p; Start-Sleep 10; Remove-Item -Path $p; Write-host "updating"; winget upgrade --all; Write-host "Done updating";for ($i = 15; $i -gt 0; $i = $i - 1) {Write-Host "Closing in $i";Start-Sleep 1};exit